client/src/pages/product-detail.tsx.content:
import { useParams } from "wouter";
import { useQuery } from "@tanstack/react-query";
import { Product } from "@shared/schema";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Skeleton } from "@/components/ui/skeleton";
import { Heart, ArrowLeft, Check } from "lucide-react";
import { useCart } from "@/contexts/cart-context";
import { useAuth } from "@/contexts/auth-context";
import { useLocation } from "wouter";

export default function ProductDetail() {
  const params = useParams();
  const productId = params.id;
  const { addToCart, isLoading: cartLoading } = useCart();
  const { user } = useAuth();
  const [, setLocation] = useLocation();

  const { data: product, isLoading, error } = useQuery({
    queryKey: ["/api/products", productId],
    queryFn: async () => {
      const response = await fetch(`/api/products/${productId}`);
      if (!response.ok) {
        throw new Error("Product not found");
      }
      return response.json() as Promise<Product>;
    },
    enabled: !!productId,
  });

  const handleAddToCart = async () => {
    if (!user) {
      setLocation("/login");
      return;
    }
    if (product) {
      await addToCart(product.id);
    }
  };

  if (isLoading) {
    return (
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-12">
          <Skeleton className="aspect-square rounded-lg" />
          <div className="space-y-4">
            <Skeleton className="h-8 w-3/4" />
            <Skeleton className="h-20 w-full" />
            <Skeleton className="h-12 w-32" />
            <Skeleton className="h-32 w-full" />
            <div className="flex gap-4">
              <Skeleton className="h-12 flex-1" />
              <Skeleton className="h-12 w-12" />
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (error || !product) {
    return (
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <Card>
          <CardContent className="p-8 text-center">
            <h1 className="text-2xl font-bold text-gray-900 mb-4">Product Not Found</h1>
            <p className="text-gray-600 mb-6">The product you're looking for doesn't exist.</p>
            <Button onClick={() => setLocation("/")} variant="outline">
              <ArrowLeft className="w-4 h-4 mr-2" />
              Back to Home
            </Button>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <Button
        onClick={() => setLocation("/")}
        variant="ghost"
        className="mb-6"
      >
        <ArrowLeft className="w-4 h-4 mr-2" />
        Back to Products
      </Button>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-12">
        {/* Product Image */}
        <div className="aspect-square rounded-lg overflow-hidden bg-gray-100">
          <img
            src={product.imageUrl}
            alt={product.title}
            className="w-full h-full object-cover"
          />
        </div>

        {/* Product Info */}
        <div className="flex flex-col">
          <h1 className="text-3xl font-bold text-gray-900 mb-4">{product.title}</h1>
          <p className="text-gray-600 text-lg mb-6">{product.description}</p>

          <div className="mb-6">
            <span className="text-4xl font-bold text-gray-900">${product.price}</span>
          </div>

          <div className="mb-6">
            <span className={`inline-block px-3 py-1 rounded-full text-sm ${
              (product.stock || 0) > 0 
                ? "bg-green-100 text-green-800" 
                : "bg-red-100 text-red-800"
            }`}>
              {(product.stock || 0) > 0 ? `${product.stock || 0} in stock` : "Out of stock"}
            </span>
          </div>

          <div className="mb-6">
            <h3 className="text-lg font-semibold mb-3">Category:</h3>
            <span className="inline-block px-4 py-2 bg-blue-100 text-blue-800 rounded-lg capitalize">
              {product.category}
            </span>
          </div>

          <div className="flex items-center space-x-4">
            <Button
              data-testid="detail-add-to-cart"
              onClick={handleAddToCart}
              disabled={cartLoading || (product.stock || 0) === 0}
              className="flex-1 bg-blue-600 text-white py-3 px-6 rounded-lg hover:bg-blue-700 font-semibold"
            >
              {(product.stock || 0) === 0 ? "Out of Stock" : "Add to Cart"}
            </Button>
            <Button
              variant="outline"
              size="icon"
              className="p-3 border border-gray-300 rounded-lg hover:bg-gray-50"
            >
              <Heart className="w-5 h-5 text-gray-600" />
            </Button>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/components/layout/navbar.tsx.content:
import { Link, useLocation } from "wouter";
import { ShoppingCart, Search, User, LogOut } from "lucide-react";
import { useAuth } from "@/hooks/use-auth";
import { useCart } from "@/hooks/use-cart";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { useState } from "react";

interface NavbarProps {
  searchTerm: string;
  onSearchChange: (term: string) => void;
}

export function Navbar({ searchTerm, onSearchChange }: NavbarProps) {
  const [location, setLocation] = useLocation();
  const { user, logout } = useAuth();
  const { getTotalItems } = useCart();

  const handleLogout = () => {
    logout();
    setLocation("/");
  };

  return (
    <nav className="bg-white shadow-sm border-b border-gray-200 sticky top-0 z-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-16">
          <div className="flex items-center space-x-8">
            <div className="flex-shrink-0">
              <Link href="/">
                <h1 className="text-2xl font-bold text-gray-900 cursor-pointer">ModernStore</h1>
              </Link>
            </div>
            <div className="hidden md:block">
              <div className="flex items-center space-x-4">
                <Link href="/" className={location === "/" ? "text-gray-900 hover:text-blue-600 font-medium" : "text-gray-600 hover:text-blue-600"}>
                  Home
                </Link>
                {user?.isAdmin && (
                  <Link href="/admin" className={location === "/admin" ? "text-gray-900 hover:text-blue-600 font-medium" : "text-gray-600 hover:text-blue-600"}>
                    Admin
                  </Link>
                )}
              </div>
            </div>
          </div>

          <div className="flex items-center space-x-4">
            {/* Search Bar - Only show on home page */}
            {location === "/" && (
              <div className="hidden sm:block relative">
                <Input
                  type="text"
                  placeholder="Search products..."
                  data-testid="search-input"
                  value={searchTerm}
                  onChange={(e) => onSearchChange(e.target.value)}
                  className="w-64 pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                />
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
              </div>
            )}

            {user ? (
              <>
                {/* Cart Icon */}
                <Button
                  variant="ghost"
                  size="icon"
                  data-testid="cart-button"
                  onClick={() => setLocation("/cart")}
                  className="relative p-2 text-gray-600 hover:text-blue-600"
                >
                  <ShoppingCart className="w-5 h-5" />
                  {getTotalItems() > 0 && (
                    <span className="absolute -top-1 -right-1 bg-blue-600 text-white text-xs rounded-full h-5 w-5 flex items-center justify-center">
                      {getTotalItems()}
                    </span>
                  )}
                </Button>

                {/* User Menu */}
                <div className="flex items-center space-x-2">
                  <span className="text-sm text-gray-600">
                    Hello, {user.firstName || user.email}
                  </span>
                  <Button
                    variant="ghost"
                    size="icon"
                    onClick={handleLogout}
                    className="text-gray-600 hover:text-blue-600"
                  >
                    <LogOut className="w-4 h-4" />
                  </Button>
                </div>
              </>
            ) : (
              <div className="flex items-center space-x-2">
                <Button
                  variant="ghost"
                  data-testid="login-button"
                  onClick={() => setLocation("/login")}
                  className="text-gray-600 hover:text-blue-600 font-medium"
                >
                  Login
                </Button>
                <span className="text-gray-400">|</span>
                <Button
                  data-testid="register-button"
                  onClick={() => setLocation("/login?mode=register")}
                  className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 font-medium"
                >
                  Sign Up
                </Button>
              </div>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
}

